import {React, useState, useEffect} from 'react';
import "./stations.css";
import axios from "axios";

const Stations = () => {
  const [listOfStations, setListOfStations] = useState([]);
  const [loading, setLoading] = useState(false);
  const [next, setNext] = useState(0);

  /*useEffect(() => {
    fetchNextPage(next);
  }, [])

  const fetchNextPage = async(next) => {
    console.log(next)
    setLoading(true);
    axios.get("http://localhost:3001/getNextDataadad", { params: { next } }).then((response) => {
      console.log(response.data)
      setListOfStations(response.data)
      setLoading(false);
    })
  }

  const handlePagination = (numOfPages) => {
      let i = next + numOfPages;
      if(i >= 0){
        setNext(i)
        fetchNextPage(i)
      }
  }

  if (loading) {
    return <h2>Loading...</h2>;
  } */

  return (
    <div className="routes-list-wrapper">
      {listOfStations.map((station) => {
        return (
            <div>
                <p>Station</p>
            </div>
        );
      })}
        <div className="pagination-section">
          <button className="btn" onClick={() => { handlePagination(-15) }}>last</button>
          <button className="btn" onClick={() => { handlePagination(15) }}>next</button>
        </div>
    </div>
  );
};

export default Stations;
