import {React, image} from 'react';
import "./routes.css";
const start = require("https://www.flaticon.com/free-icons/map");
const Routes = ({ posts, loading }) => {
  if (loading) {
    return <h2>Loading...</h2>;
  }

  return (
          <div className="RoutesListWrapper">
          {posts.map((route) => {
            return(
              <div key={route.id} className="RouteCard">
                <div className='route-item-section'>
                  <image source={start}></image>
                  <p>Start location: {route.departurestation}</p>
                </div>
                <p>Return location: {route.returnstation}</p>
                <p>Distance: {route.distance}</p>
                <p>Duration: {route.duration}</p>
              </div>
            )
          })}
        </div>
    
  );
};

export default Routes;
