import "./App.css";
import {useState, useEffect} from 'react';
import axios from "axios";
import Pagination from "./components/Pagination";
import Routes from "./components/Routes";
import NavBar from "./components/navbar/Navbar";
import fetchChunk from "fetch-chunk";

function App() {
  const [listOfRoutes, setListOfRoutes] = useState([]);
  const [loading, setLoading] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [postsPerPage] = useState(4);

  const [next, setNext] = useState(4);

  useEffect(() => {
    //setLoading(true);
    /*axios.get("http://localhost:3001/getData").then((response) => {
      setListOfRoutes(response.data)
      setLoading(false);
    })*/
    //fetchNextPage(next);
  }, [])

  const fetchNextPage = async(next) => {
    console.log(next)
    setLoading(true);
    axios.get("http://localhost:3001/getNextData", { params: { next } }).then((response) => {
      setListOfRoutes(response.data)
      setLoading(false);
    })
  }
    // Get current posts
    const indexOfLastPost = currentPage * postsPerPage;
    const indexOfFirstPost = indexOfLastPost - postsPerPage;
    const currentPosts = listOfRoutes.slice(indexOfFirstPost, indexOfLastPost);

    // Change page
    const paginate = pageNumber => setCurrentPage(pageNumber);

  return (
    <div className="App">
      <NavBar></NavBar>
      <h1>Routes</h1>
      <button onClick={()=>{fetchNextPage(next)}}>Load next</button>

      <div className="RoutesListWrapper">
        <Routes posts={currentPosts} loading={loading} />
        <Pagination
        postsPerPage={postsPerPage}
        totalPosts={listOfRoutes.length}
        paginate={paginate}
        />
      </div>
    </div>
  );
}

export default App;
