import axios from "axios";

export const HandleSearch = async(parameter, endpoint, {setValue}) => {
    axios.get("http://localhost:3001/" + endpoint, { params: { parameter } }).then((response) => {  
      setValue(response.data);
      
      // Stations page has chained calculation function hooked up to it.
      if(endpoint === 'getStationByName'){
        calculateStationJourneys(response.data[0].ID);
      }
    })
}

const calculateStationJourneys = (stationid) => {
    axios.get("http://localhost:3001/getStationJourneys", { params: { stationid } }).then((response) => {
      setStationJourneyData(response.data);
    })
}
