import {React, useState, useEffect} from 'react';
import "./stations.css";
import axios from "axios";
import postItImage from './stations-images/post-it2.png';

const Stations = () => {
  const [listOfStations, setListOfStations] = useState([]);
  const [loading, setLoading] = useState(false);
  const [next, setNext] = useState(0);

  useEffect(() => {
    fetchNextPage(next);
  }, [])

  const fetchNextPage = async(next) => {
    console.log(next)
    setLoading(true);
    axios.get("http://localhost:3001/getStations", { params: { next } }).then((response) => {
      console.log(response.data)
      setListOfStations(response.data)
      setLoading(false);
    })
  } 

  const handlePagination = (numOfPages) => {
      let i = next + numOfPages;
      if(i >= 0){
        setNext(i)
        fetchNextPage(i)
      }
  }

  if (loading) {
    return <h2>Loading...</h2>;
  } 

  return (
    <div className="stations-list-wrapper">
      {listOfStations.map((station, id) => {
        return (
            <div id={id} className="station-item">
              <div className="station-item-section" style={{backgroundImage: `url(${postItImage})`, backgroundRepeat: `no-repeat`, backgroundSize: `cover`}}>
                <p>Station name</p>
                <p>{station.Nimi}</p>
              </div>
              <div className="station-item-section">
                <p>Station address</p>
                <p>{station.Osoite}</p>
              </div>

              <div className="station-item-section">
                <p>Total journeys started</p>
                <p>{station.Nimi}</p>
              </div>

              <div className="station-item-section">
                <p>Total journeys ended</p>
                <p>{station.Nimi}</p>
              </div>
            </div>
        );
      })}
        <div className="pagination-section">
          <button className="btn" onClick={() => { handlePagination(-4) }}>last</button>
          <button className="btn" onClick={() => { handlePagination(4) }}>next</button>
        </div>
    </div>
  );
};

export default Stations;
